// --- Iterate through RSS items ---
for (let i = 0; i < itemsToUse.length; i++) {
  const paper = itemsToUse[i].json;
  const title = paper.title || "No title";
  const description =
    paper.contentSnippet || paper.description || paper.content || paper.summary || "";
  const link = paper.link || "";

  if (keywords.some(kw => (title + description).toLowerCase().includes(kw.toLowerCase()))) {
    summaries += `Paper ${i + 1}:\nTitle: ${title}\nAbstract: ${description}\nLink: ${link}\n\n`;
  }
}

// --- If nothing matched, fallback to first 5 papers ---
if (!summaries) {
  for (let i = 0; i < itemsToUse.length; i++) {
    const paper = itemsToUse[i].json;
    summaries += `Paper ${i + 1}:\nTitle: ${paper.title}\nLink: ${paper.link}\n\n`;
  }
}

return [{ json: { summaries } }];
